CREATE OR REPLACE FUNCTION pls_obter_seq_cmed_resumo (nr_seq_conta_p pls_conta_medica_resumo.nr_seq_conta%type) RETURNS bigint AS $body$
DECLARE

nr_retorno_w	pls_conta_medica_resumo.nr_sequencia%type;
ds_sql_w	varchar(100);
qt_registro_w	integer;


BEGIN
qt_registro_w := 0;
-- feito com SQL dinâmico para não deixar muitos objetos inválidos na base durante a atualização, pois a sequence não existe e vai ser criada pela tasy_preparar_base
-- depois de um tempo pode ser retirado esse SQL dinâmico e fazer a chamada direto na sequence para melhorar a performance.
-- dlehmkuhl 25/04/2013 OS 584859
begin
	ds_sql_w := 'select pls_conta_medica_resumo_seq.nextval from dual';
	EXECUTE ds_sql_w into STRICT nr_retorno_w;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(237795);
end;

select	count(1)
into STRICT	qt_registro_w
from	pls_conta_medica_resumo
where	nr_seq_conta	= nr_seq_conta_p
and	nr_sequencia	= nr_retorno_w;

if (qt_registro_w > 0) then
	-- chama novamente até encontrar um valor que não esteja sendo usado
	nr_retorno_w := pls_obter_seq_cmed_resumo(nr_seq_conta_p);
end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
