CREATE OR REPLACE FUNCTION pls_obter_seq_codigo_coop ( nr_seq_cooperativa_p bigint, cd_cooperativa_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(15);


BEGIN

if (nr_seq_cooperativa_p IS NOT NULL AND nr_seq_cooperativa_p::text <> '') then
	select	max(cd_cooperativa)
	into STRICT	ds_retorno_w
	from	pls_congenere
	where	nr_sequencia	= nr_seq_cooperativa_p;

elsif (cd_cooperativa_p IS NOT NULL AND cd_cooperativa_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	pls_congenere
	where	(cd_cooperativa)::numeric  = (cd_cooperativa_p)::numeric;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
