CREATE OR REPLACE FUNCTION pls_obter_seq_cod_material_web ( nr_seq_material_p text, cd_material_ops_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

if (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
	select	max(cd_material_ops)
	into STRICT	ds_retorno_w
	from	pls_material
	where	nr_sequencia	= nr_seq_material_p
	and (coalesce(dt_limite_utilizacao::text, '') = '' or dt_limite_utilizacao > clock_timestamp())
	and (coalesce(dt_exclusao::text, '') = '' or dt_exclusao > clock_timestamp())
	and (coalesce(dt_inclusao::text, '') = '' or trunc(dt_inclusao) <= trunc(clock_timestamp()));

elsif (cd_material_ops_p IS NOT NULL AND cd_material_ops_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	pls_material
	where	cd_material_ops	= cd_material_ops_p
	and (coalesce(dt_limite_utilizacao::text, '') = '' or dt_limite_utilizacao > clock_timestamp())
	and (coalesce(dt_exclusao::text, '') = '' or dt_exclusao > clock_timestamp())
	and (coalesce(dt_inclusao::text, '') = '' or trunc(dt_inclusao) <= trunc(clock_timestamp()));
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
