SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_seq_congenere ( nr_seq_segurado_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

 
nr_seq_congenere_w		varchar(10);
nm_congenere_w			varchar(255);
nr_seq_conge_w			bigint;


BEGIN 
if (ie_tipo_p	= 'C') then 
	select	to_char(nr_seq_congenere) 
	into STRICT	nr_seq_congenere_w 
	from 	pls_segurado 
	where	nr_sequencia = nr_seq_segurado_p;
	 
	return	nr_seq_congenere_w;
elsif (ie_tipo_p	= 'N') then 
	select	nr_seq_congenere 
	into STRICT	nr_seq_conge_w 
	from 	pls_segurado 
	where	nr_sequencia = nr_seq_segurado_p;
 
	select	substr(obter_nome_pf_pj(null, cd_cgc),1,255) 
	into STRICT	nm_congenere_w 
	from	pls_congenere 
	where	nr_sequencia	= nr_seq_conge_w;
 
	return	nm_congenere_w;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
