CREATE OR REPLACE FUNCTION pls_obter_seq_material_unimed ( cd_material_ops_p text) RETURNS bigint AS $body$
DECLARE


nr_retorno_w		bigint;


BEGIN

select 	max(nr_sequencia)
into STRICT	nr_retorno_w
from (
	SELECT  nr_sequencia
	from  	pls_material
	where  	cd_material_ops  = cd_material_ops_p
	and  	ie_situacao = 'A'
	and  	coalesce(dt_exclusao::text, '') = ''
	
union all

	SELECT  nr_sequencia
	from  	pls_material
	where  	cd_material_ops  = cd_material_ops_p
	and  	ie_situacao = 'A'
	and  	dt_exclusao > clock_timestamp()) alias3;

return	nr_retorno_w;

end;
