CREATE OR REPLACE FUNCTION pls_obter_seq_mot_saida_tiss ( cd_motivo_saida_p pls_motivo_saida.cd_tiss%type, cd_versao_tiss_p pls_motivo_saida.ie_versao%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS bigint AS $body$
DECLARE


nr_seq_motivo_saida_w	pls_motivo_saida.nr_sequencia%type;


BEGIN

select	max(t.nr_sequencia)
into STRICT	nr_seq_motivo_saida_w
from (	SELECT	a.nr_sequencia
	from	pls_motivo_saida	a
	where	a.cd_tiss		= cd_motivo_saida_p
	and	a.ie_versao		= cd_versao_tiss_p
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	
union all

	SELECT	a.nr_sequencia
	from	pls_motivo_saida		a,
		pls_motivo_saida_ver_tiss	b
	where	b.nr_seq_motivo_enc 	= a.nr_sequencia
	and	b.ie_Versao		= cd_versao_tiss_p
	and	b.ie_situacao 		= 'A'
	and	a.cd_tiss		= cd_motivo_saida_p
	and	a.cd_estabelecimento	= cd_estabelecimento_p ) t;

if (coalesce(nr_seq_motivo_saida_w::text, '') = '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_motivo_saida_w
	from	pls_motivo_saida
	where	cd_tiss = cd_motivo_saida_p
	and	cd_estabelecimento	= cd_estabelecimento_p;
end if;

return	nr_seq_motivo_saida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
