CREATE OR REPLACE FUNCTION pls_obter_seq_prot_conta ( nr_seq_conta_coparticipacao_p bigint, nr_seq_conta_co_p bigint, nr_seq_conta_pos_estab_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE

/* 
ie_opcao_p 
'P' - Seq Protocolo 
'C' - Seq Conta						 
*/
 
									 
nr_seq_protocolo_w		varchar(50);
nr_seq_conta_w			varchar(50);


BEGIN 
if (ie_opcao_p = 'P')	then 
	if (nr_seq_conta_coparticipacao_p IS NOT NULL AND nr_seq_conta_coparticipacao_p::text <> '')	then 
		select	nr_seq_protocolo 
		into STRICT	nr_seq_protocolo_w 
		from	pls_conta_coparticipacao 
		where	nr_sequencia = nr_seq_conta_coparticipacao_p;
	elsif (nr_seq_conta_co_p IS NOT NULL AND nr_seq_conta_co_p::text <> '')	then 
		select	nr_seq_protocolo 
		into STRICT	nr_seq_protocolo_w 
		from	pls_conta_co 
		where	nr_sequencia = nr_seq_conta_co_p;
	elsif (nr_seq_conta_pos_estab_p IS NOT NULL AND nr_seq_conta_pos_estab_p::text <> '')	then 
		select	nr_seq_protocolo 
		into STRICT	nr_seq_protocolo_w 
		from	pls_conta_pos_estabelecido 
		where	nr_sequencia = nr_seq_conta_pos_estab_p;
	end if;
end if;
 
if (ie_opcao_p = 'C')	then 
	if (nr_seq_conta_coparticipacao_p IS NOT NULL AND nr_seq_conta_coparticipacao_p::text <> '')	then 
		select	nr_seq_conta 
		into STRICT	nr_seq_conta_w 
		from  pls_conta_coparticipacao 
		where  nr_sequencia = nr_seq_conta_coparticipacao_p;
	elsif (nr_seq_conta_co_p IS NOT NULL AND nr_seq_conta_co_p::text <> '')	then 
		select	nr_seq_conta 
		into STRICT	nr_seq_conta_w 
		from  pls_conta_co 
		where  nr_sequencia = nr_seq_conta_co_p;
	elsif (nr_seq_conta_pos_estab_p IS NOT NULL AND nr_seq_conta_pos_estab_p::text <> '')	then 
		select	nr_seq_conta 
		into STRICT	nr_seq_conta_w 
		from  pls_conta_pos_estabelecido 
		where  nr_sequencia = nr_seq_conta_pos_estab_p;
	end if;
end if;
 
if (ie_opcao_p = 'P')	then 
	return nr_seq_protocolo_w;
else 
	return nr_seq_conta_w;
end if;
 
end;
$body$
