CREATE OR REPLACE FUNCTION pls_obter_seq_ret_rec_glosa ( nr_seq_prestador_p pls_regra_ret_rec_glosa.nr_seq_prestador%type, cd_estabelecimento_p pls_regra_ret_rec_glosa.cd_estabelecimento%type) RETURNS bigint AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade:	Retorna a regra que se controla o retorno de recurso de glosa em XML. 
	Caso é encontrada mais de uma, será utilizada a mais recente 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[X] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
------------------------------------------------------------------------------------------------------------------- 
Pontos de atenção: 
Alterações: 
------------------------------------------------------------------------------------------------------------------- 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
tb_nr_seq_regra_w	pls_util_cta_pck.t_number_table;
nr_seq_regra_w		pls_regra_ret_rec_glosa.nr_sequencia%type;

-- Cursor com as regras, ordenando da mais especifica para menos 
c01 CURSOR(	nr_seq_prestador_pc	pls_regra_ret_rec_glosa.nr_seq_prestador%type, 
		cd_estabelecimento_pc	pls_regra_ret_rec_glosa.cd_estabelecimento%type) FOR 
	SELECT	t.nr_sequencia 
	from (	SELECT	a.nr_sequencia, 
			trunc(a.dt_inicio_vigencia, 'dd') dt_inicio_vigencia, 
			fim_dia(a.dt_fim_vigencia_ref) dt_fim_vigencia, 
			0 nr_ordem 
		from	pls_regra_ret_rec_glosa	a 
		where	a.nr_seq_prestador	= nr_seq_prestador_p 
		and	cd_estabelecimento	= cd_estabelecimento_p 
		
union all
 
		select	a.nr_sequencia, 
			trunc(a.dt_inicio_vigencia, 'dd') dt_inicio_vigencia, 
			fim_dia(a.dt_fim_vigencia_ref) dt_fim_vigencia, 
			1 nr_ordem 
		from	pls_regra_ret_rec_glosa	a, 
			pls_preco_prestador	b 
		where	b.nr_seq_grupo		= a.nr_seq_grupo_prestador 
		and	b.nr_seq_prestador	= nr_seq_prestador_p 
		and	cd_estabelecimento	= cd_estabelecimento_p) t 
	where	trunc(clock_timestamp(), 'dd') between t.dt_inicio_vigencia and t.dt_fim_vigencia 
	order by t.nr_ordem desc;
		

BEGIN 
 
-- Abre o curos de regras 
open c01(nr_seq_prestador_p, cd_estabelecimento_p);
loop 
fetch c01 bulk collect into tb_nr_seq_regra_w 
limit pls_util_pck.qt_registro_transacao_w;
 
exit when tb_nr_seq_regra_w.count = 0;
 
	--Atribui a ultimo regra cadastrada da listagem , quando o cursor acabar, vai ficar apenas a regra mais especifica 
	nr_seq_regra_w := tb_nr_seq_regra_w(tb_nr_seq_regra_w.last);
	tb_nr_seq_regra_w.delete;
 
end loop;
 
		 
 
return nr_seq_regra_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION pls_obter_seq_ret_rec_glosa ( nr_seq_prestador_p pls_regra_ret_rec_glosa.nr_seq_prestador%type, cd_estabelecimento_p pls_regra_ret_rec_glosa.cd_estabelecimento%type) FROM PUBLIC;

