SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_seq_segurado_tarja ( cd_usuario_plano_p text, cd_estabelecimento_p bigint, ie_restringe_estab_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_segurado_w	bigint;
ie_tipo_repasse_w	varchar(1);
cd_estabelecimento_w	smallint;
qt_regra_eventual_w	bigint;


BEGIN

if (ie_restringe_estab_p = 'S') then
	cd_estabelecimento_w	:= cd_estabelecimento_p;
else
	cd_estabelecimento_w	:= null;
end if;

/* 1º Busca pelo NR_CARTAO_INTERCAMBIO - Somente retornar se tiver ativo, senão deve ser criado usuário eventual */

select	max(b.nr_sequencia)
into STRICT	nr_seq_segurado_w
from	pls_segurado		b,
	pls_segurado_carteira	a
where	a.nr_seq_segurado	= b.nr_sequencia
and	b.ie_situacao_atend	= 'A'
and	((a.cd_estabelecimento = cd_estabelecimento_w) or (coalesce(cd_estabelecimento_w::text, '') = ''))
and	a.nr_cartao_intercambio	= cd_usuario_plano_p;

/* 2º Busca pelo CD_USUARIO_PLANO - Se existir algum cadastrado com a carteira, tem que retornar */

if (coalesce(nr_seq_segurado_w::text, '') = '') then
	select	max(b.nr_sequencia)
	into STRICT	nr_seq_segurado_w
	from	pls_segurado		b,
		pls_segurado_carteira	a
	where	a.nr_seq_segurado	= b.nr_sequencia
	and	((a.cd_estabelecimento = cd_estabelecimento_w) or (coalesce(cd_estabelecimento_w::text, '') = ''))
	and	a.cd_usuario_plano	= cd_usuario_plano_p;

	if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then
		select	max(ie_tipo_repasse)
		into STRICT	ie_tipo_repasse_w
		from	pls_segurado
		where	nr_sequencia	= nr_seq_segurado_w;

		if (ie_tipo_repasse_w IS NOT NULL AND ie_tipo_repasse_w::text <> '') then
			select	count(1)
			into STRICT	qt_regra_eventual_w
			from	pls_regra_usuario_repasse
			where	ie_tipo_repasse	= ie_tipo_repasse_w
			and	ie_gerar_usuario_eventual = 'S'
			and	ie_situacao = 'A';

			if (qt_regra_eventual_w > 0) then
				nr_seq_segurado_w	:= null;
			end if;
		end if;
	end if;
end if;

return	nr_seq_segurado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
