CREATE OR REPLACE FUNCTION pls_obter_seq_valor_sca_benef ( nr_seq_segurado_p bigint, nr_seq_plano_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_seq_segurado_preco_w		bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_segurado_preco_w
from	pls_segurado_preco_origem
where	nr_seq_segurado = nr_seq_segurado_p
and	nr_seq_plano	= nr_seq_plano_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	(((dt_referencia_p >= trunc(dt_reajuste, 'month')) and (cd_motivo_reajuste	<> 'E'))
	or	((cd_motivo_reajuste	= 'E') and (dt_referencia_p >= trunc(add_months(dt_reajuste,1), 'month'))));

return	nr_seq_segurado_preco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
