CREATE OR REPLACE FUNCTION pls_obter_se_acesso_mala ( nr_seq_tipo_mala_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1)	:= 'N';
qt_regras_w			bigint;
qt_liberados_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_regras_w
from	pls_acesso_mala_direta
where	nr_seq_tipo_mala	= nr_seq_tipo_mala_p;

if (qt_regras_w	> 0) then
	select	count(*)
	into STRICT	qt_liberados_w
	from	pls_acesso_mala_direta
	where (cd_perfil		= coalesce(cd_perfil_p,0)
	or	nm_usuario_param	= coalesce(nm_usuario_p,'0'))
	and	nr_seq_tipo_mala	= nr_seq_tipo_mala_p;

	if (qt_liberados_w	> 0) then
		ds_retorno_w	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;
else
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
