CREATE OR REPLACE FUNCTION pls_obter_se_altera_fx_etaria ( nr_seq_segurado_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


/* ESSA FUNCTION RETORNA SE O BENEFICIÁRIO ESTÁ ALTERANDO A FAIXA ETÁRIA NO MÊS */

ds_retorno_w		varchar(255);

dt_nascimento_w		timestamp;
nr_seq_tabela_w		bigint;
dt_referencia_w		timestamp;
qt_idade_w		bigint;
qt_idade_ant_w		bigint;
nr_seq_preco_w		bigint;
nr_seq_preco_ant_w	bigint;
vl_preco_atual_w	double precision;
vl_preco_ant_w		double precision;


BEGIN
begin
select	a.dt_nascimento,
	b.nr_seq_tabela
into STRICT	dt_nascimento_w,
	nr_seq_tabela_w
from	pessoa_fisica	a,
	pls_segurado	b
where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
and	b.nr_sequencia		= nr_seq_segurado_p;
exception
when others then
	dt_nascimento_w	:= null;
	nr_seq_tabela_w	:= null;
end;

if (to_char(dt_nascimento_w,'mm') = to_char(dt_referencia_p,'mm')) then
	dt_referencia_w	:= fim_dia(last_day(dt_referencia_p));

	qt_idade_w	:= trunc(months_between(dt_referencia_w, trunc(dt_nascimento_w,'month')) / 12);
	qt_idade_ant_w	:= qt_idade_w - 1;

	SELECT * FROM PLS_OBTER_FAIXA_ETARIA(nr_seq_tabela_w, qt_idade_w, nr_seq_preco_w, vl_preco_atual_w) INTO STRICT nr_seq_preco_w, vl_preco_atual_w;
	SELECT * FROM PLS_OBTER_FAIXA_ETARIA(nr_seq_tabela_w, qt_idade_ant_w, nr_seq_preco_ant_w, vl_preco_ant_w) INTO STRICT nr_seq_preco_ant_w, vl_preco_ant_w;

	if (nr_seq_preco_w <> nr_seq_preco_ant_w) then
		ds_retorno_w	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;
else
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
