CREATE OR REPLACE FUNCTION pls_obter_se_alt_compl ( cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, ie_tipo_compl_p bigint, cd_perfil_ativo_p bigint, cd_funcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter regra de alteração de endereço da pessoa física  em OPS - Cadastro de Regras / OPS - Cadastro de pessoas
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  x ] Tasy (Delphi/Java) [   ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/nr_seq_regra_w			bigint;
ie_permite_alterar_w		varchar(1);
ds_retorno_w			varchar(1) := 'S';
ie_tipo_contrat_benef_w		varchar(2);
ie_tipo_contrat_regra_w		varchar(2);
cd_funcao_w			integer;

C01 CURSOR FOR
	SELECT	a.ie_tipo_contratacao
	from	pls_contrato		c,
		pls_segurado		b,
		pls_plano		a
	where	a.nr_sequencia		= b.nr_seq_plano
	and	c.nr_sequencia		= b.nr_seq_contrato
	and	b.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
	and	((coalesce(c.dt_rescisao_contrato::text, '') = '') or (c.dt_rescisao_contrato > to_date(clock_timestamp())));

C02 CURSOR FOR
	SELECT 		ie_permite_alterar,
			ie_tipo_contratacao
	from 		pls_regra_compl_pf
	where		((cd_perfil 		= cd_perfil_ativo_p) or (coalesce(cd_perfil::text, '') = ''))
	and		((cd_funcao		= cd_funcao_p) or (coalesce(cd_funcao::text, '') = ''))
	and		cd_estabelecimento 	= cd_estabelecimento_p
	order by	coalesce(cd_perfil,0),
			coalesce(ie_tipo_contratacao, '0');



BEGIN
open C01;
loop
fetch C01 into
	ie_tipo_contrat_benef_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		ie_permite_alterar_w,
		ie_tipo_contrat_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		if	(((ie_tipo_contrat_benef_w = ie_tipo_contrat_regra_w) or (coalesce(ie_tipo_contrat_regra_w::text, '') = '')) and (ie_permite_alterar_w = 'N')) then
			ds_retorno_w	:= 'N';
		end if;
		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
