CREATE OR REPLACE FUNCTION pls_obter_se_alt_dt_rescisao ( dt_rescisao_p timestamp, nr_seq_contrato_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
dt_rescisao_w		timestamp;


BEGIN

ds_retorno_w := 'N';

select	max(dt_rescisao)
into STRICT	dt_rescisao_w
from	pls_segurado
where	nr_seq_contrato = nr_seq_contrato_p;

if	((trunc(dt_rescisao_w,'dd') > trunc(dt_rescisao_p,'dd')) and (dt_rescisao_w IS NOT NULL AND dt_rescisao_w::text <> '')) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
