SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_analise_adesao ( nm_tabela_p text, nr_seq_pergunta_p bigint, nr_seq_restricao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_w	integer;
ie_retorno_w	varchar(1)	:= 'N';
nr_seq_superior_w	bigint;


BEGIN

select	count(1)
into STRICT	qt_reg_w
from	pergunta
where	ie_componente	= 'lcb'
and	nr_sequencia	= nr_seq_pergunta_p;

if (qt_reg_w > 0) then
	select	count(1)
	into STRICT	qt_reg_w
	from	pergunta_result
	where	nr_seq_pergunta		= nr_seq_pergunta_p
	and	ie_analise_adesao	= 'S';

	if (qt_reg_w > 0) then
		if (nm_tabela_p = 'PLS_DECLARACAO_RESPOSTA') then
			select	count(*)
			into STRICT	qt_reg_w
			from	pls_declaracao_resposta	a,
				pergunta		b,
				pergunta_result		c
			where	a.nr_seq_pergunta 	= b.nr_sequencia
			and	c.nr_seq_pergunta	= b.nr_sequencia
			and	a.ds_resultado		= c.cd_item
			and	c.ie_analise_adesao	= 'S'
			and	a.nr_seq_declaracao	= nr_seq_restricao_p
			and	a.nr_seq_pergunta	= nr_seq_pergunta_p;

			if (qt_reg_w > 0) then
				ie_retorno_w := 'S';
			end if;
		end if;
	end if;
else
	select	max(nr_seq_superior)
	into STRICT	nr_seq_superior_w
	from	pergunta
	where	nr_sequencia	= nr_seq_pergunta_p;

	if (nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') then
		select	count(1)
		into STRICT	qt_reg_w
		from	pergunta
		where	ie_componente	= 'lcb'
		and	nr_sequencia	= nr_seq_superior_w;

		if (qt_reg_w > 0) then
			select	count(1)
			into STRICT	qt_reg_w
			from	pergunta_result
			where	nr_seq_pergunta		= nr_seq_superior_w
			and	ie_analise_adesao	= 'S';

			if (qt_reg_w > 0) then
				if (nm_tabela_p = 'PLS_DECLARACAO_RESPOSTA') then
					select	count(*)
					into STRICT	qt_reg_w
					from	pls_declaracao_resposta	a,
						pergunta		b,
						pergunta_result		c
					where	a.nr_seq_pergunta 	= b.nr_sequencia
					and	c.nr_seq_pergunta	= b.nr_sequencia
					and	a.ds_resultado		= c.cd_item
					and	c.ie_analise_adesao	= 'S'
					and	a.nr_seq_declaracao	= nr_seq_restricao_p
					and	a.nr_seq_pergunta	= nr_seq_superior_w;

					if (qt_reg_w > 0) then
						ie_retorno_w := 'S';
					end if;
				end if;
			end if;
		end if;
	end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
