CREATE OR REPLACE FUNCTION pls_obter_se_analise_intercamb ( nr_seq_auditoria_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_processo_w		varchar(2);
ie_tipo_intercambio_w		varchar(10);
ie_retorno_w			varchar(2)	:= 'N';

nr_seq_guia_w			bigint;
nr_seq_requisicao_w		bigint;

qt_registros_w			bigint;


BEGIN

begin
select	coalesce(nr_seq_guia,0),
	coalesce(nr_seq_requisicao,0)
into STRICT	nr_seq_guia_w,
	nr_seq_requisicao_w
from	pls_auditoria
where	nr_sequencia	= nr_seq_auditoria_p;
exception
when others then
	nr_seq_guia_w		:= 0;
	nr_seq_requisicao_w	:= 0;
end;

if (nr_seq_guia_w	<> 0) then
	select	ie_tipo_processo,
		ie_tipo_intercambio
	into STRICT	ie_tipo_processo_w,
		ie_tipo_intercambio_w
	from	pls_guia_plano
	where	nr_sequencia	= nr_seq_guia_w;

	if (ie_tipo_processo_w	= 'I') then
		ie_retorno_w	:= 'S';
	end if;
elsif (nr_seq_requisicao_w	<> 0) then
	select	ie_tipo_processo,
		ie_tipo_intercambio
	into STRICT	ie_tipo_processo_w,
		ie_tipo_intercambio_w
	from	pls_requisicao
	where	nr_sequencia	= nr_seq_requisicao_w;

	if (ie_tipo_processo_w	= 'I') then
		ie_retorno_w	:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
