CREATE OR REPLACE FUNCTION pls_obter_se_aniver_periodo ( dt_referencia_p timestamp, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_aniversario_w		varchar(1)	:= 'N';
dt_referencia_w			timestamp;


BEGIN
begin
select	to_date(to_char(dt_referencia_p,'dd/mm') || '/' || to_char(dt_inicial_p,'yyyy'))
into STRICT	dt_referencia_w
;
exception
when others then
	if (to_char(dt_referencia_p,'dd/mm') = '29/02') then
		dt_referencia_w	:= to_date('28/02/' || to_char(dt_inicial_p,'yyyy'));
	end if;
end;

while(trunc(dt_referencia_w,'dd') < trunc(dt_inicial_p,'dd')) loop
	select	add_months(dt_referencia_w,12)
	into STRICT	dt_referencia_w
	;
end loop;

if (trunc(dt_referencia_w,'dd') between trunc(dt_inicial_p,'dd') and trunc(dt_final_p,'dd')) then
	ie_aniversario_w	:= 'S';
end if;

return	ie_aniversario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
