SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_aniv_contrato (nr_seq_segurado_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_tipo_data_p text) RETURNS varchar AS $body$
DECLARE



dt_contratacao_w		timestamp;
ie_aniversario_w		varchar(001) := 'N';
dt_aniversario_w		timestamp;
ds_ano_w			varchar(4);
dt_ano_contrato_w		timestamp;


BEGIN

select	to_char(dt_inicial_p,'yyyy')
into STRICT	ds_ano_w
;

if (ie_tipo_data_p = 'A') then
	select	coalesce(dt_reajuste,dt_contratacao),
		dt_contratacao
	into STRICT	dt_contratacao_w,
		dt_ano_contrato_w
	from	pls_segurado
	where	nr_sequencia		= nr_seq_segurado_p;

elsif (ie_tipo_data_p = 'C') then
	select	coalesce(b.dt_reajuste,b.dt_contrato),
		b.dt_contrato
	into STRICT	dt_contratacao_w,
		dt_ano_contrato_w
	from	pls_segurado	a,
		pls_contrato	b
	where	a.nr_seq_contrato	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_segurado_p;
end if;

if (dt_contratacao_w IS NOT NULL AND dt_contratacao_w::text <> '') and (trunc(dt_ano_contrato_w,'year') <> trunc(dt_inicial_p,'year'))	then
	dt_aniversario_w	:= to_date((to_char(trunc(dt_contratacao_w,'month'),'dd/mm')
						|| '/' || ds_ano_w),'dd/mm/yyyy');

	begin
	select	coalesce(max('S'),'N')
	into STRICT	ie_aniversario_w
	
	where	dt_aniversario_w between dt_inicial_p and fim_dia(dt_final_p);
	exception
		when others then
			ie_aniversario_w	:= 'N';
	end;
end if;

return	ie_aniversario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
