CREATE OR REPLACE FUNCTION pls_obter_se_aplica_reaj_benef ( nr_seq_reajuste_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);
qt_registros_w		bigint;


BEGIN

ds_retorno_w	:= 'N';

select	count(1)
into STRICT	qt_registros_w
from	pls_reajuste
where	nr_sequencia	= nr_seq_reajuste_p
and	coalesce(nr_seq_contrato::text, '') = ''
and	coalesce(nr_seq_intercambio::text, '') = '';

if (qt_registros_w > 0) then
	select  count(*)
	into STRICT	qt_registros_w
	from    pls_lote_reaj_segurado
	where   nr_seq_lote_referencia = nr_seq_reajuste_p;

	if (qt_registros_w = 0) then
		ds_retorno_w	:= 'S';
	end if;
else
	select  count(*)
	into STRICT	qt_registros_w
	from    pls_lote_reaj_segurado
	where   nr_seq_reajuste = nr_seq_reajuste_p;

	if (qt_registros_w = 0) then
		ds_retorno_w	:= 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
