CREATE OR REPLACE FUNCTION pls_obter_se_area_atuacao_prod ( nr_seq_plano_p pls_plano.nr_sequencia%type, cd_municipio_ibge_p pls_plano_area.cd_municipio_ibge%type) RETURNS varchar AS $body$
DECLARE


qt_plano_area_w		bigint	:= 0;
qt_municipio_w		bigint	:= 0;
ie_area_atuacao_w	varchar(1)	:= 'N';


BEGIN

if (cd_municipio_ibge_p IS NOT NULL AND cd_municipio_ibge_p::text <> '') then

	select	count(1)
	into STRICT	qt_plano_area_w
	from	pls_plano_area		a
	where 	a.nr_seq_plano		= nr_seq_plano_p;
	
	if (qt_plano_area_w > 0) then
	
		select	count(1)
		into STRICT	qt_municipio_w
		from (SELECT	a.cd_municipio_ibge
			from	pls_plano_area		a
			where 	a.cd_municipio_ibge	= cd_municipio_ibge_p
			and 	a.nr_seq_plano		= nr_seq_plano_p
			
union all

			SELECT	d.cd_municipio_ibge
			from	sus_municipio		d,
				pls_regiao_local	b,
				pls_regiao		a,
				pls_plano_area		c
			where	d.ds_unidade_federacao	= b.sg_uf_municipio
			and	b.nr_seq_regiao		= a.nr_sequencia
			and	coalesce(b.cd_municipio_ibge::text, '') = ''
			and	a.nr_sequencia		= c.nr_seq_regiao
			and     c.nr_seq_plano 		= nr_seq_plano_p
			and	d.cd_municipio_ibge	= cd_municipio_ibge_p
			
union all

			select	b.cd_municipio_ibge
			from	pls_regiao_local	b,
				pls_regiao		a,
				pls_plano_area		c
			where	b.nr_seq_regiao		= a.nr_sequencia
			and	a.nr_sequencia		= c.nr_seq_regiao
			and     c.nr_seq_plano 		= nr_seq_plano_p
			and	b.cd_municipio_ibge	= cd_municipio_ibge_p
			
union all

			select	b.cd_municipio_ibge
			from	sus_municipio		b,
				pls_plano_area		a
			where	b.ds_unidade_federacao	= a.sg_estado
			and 	b.cd_municipio_ibge	= cd_municipio_ibge_p
			and 	a.nr_seq_plano		= nr_seq_plano_p
			and   coalesce(a.cd_municipio_ibge::text, '') = '') alias4;
	end if;
end if;

if (coalesce(cd_municipio_ibge_p::text, '') = '') or (qt_plano_area_w = 0) or (qt_municipio_w	> 0) then
	ie_area_atuacao_w	:= 'S';
end if;

return ie_area_atuacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
