CREATE OR REPLACE FUNCTION pls_obter_se_arq_tam_valido ( qt_bytes_param_p bigint, qt_bytes_arq_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar se o tamanho do arquivo inserido esta dentro do tamanho permitido
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_retorno_w				varchar(2);
qt_tamanho_param_w			double precision;
qt_tamanho_arquiv_w			double precision;


BEGIN

select	round((qt_bytes_param_p/1024/1024)::numeric,2)
into STRICT	qt_tamanho_param_w
;

select	round((qt_bytes_arq_p/1024/1024)::numeric,2)
into STRICT	qt_tamanho_arquiv_w
;

if (qt_tamanho_param_w	< qt_tamanho_arquiv_w) then
	ie_retorno_w	:= 'N';
else
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
