CREATE OR REPLACE FUNCTION pls_obter_se_atend_possui_pos ( nr_seq_analise_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'N';
nr_seq_conta_w		bigint;


BEGIN

select	max(x.nr_sequencia)
into STRICT	nr_seq_conta_w
from	pls_conta x
where	x.nr_seq_analise = nr_seq_analise_p
and	exists	(SELECT	1
		 from	pls_conta_pos_estabelecido y
		 where	y.nr_seq_conta	= x.nr_sequencia
		and	((y.ie_situacao	= 'A') or (coalesce(y.ie_situacao::text, '') = '')));

if (nr_seq_conta_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
