CREATE OR REPLACE FUNCTION pls_obter_se_auditoria_interc ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_estagio_w		bigint;
ie_retorno_w		varchar(1)	:= 'N';


BEGIN

if (coalesce(nr_seq_requisicao_p,0)	<> 0) then
	begin
		select	ie_estagio
		into STRICT	ie_estagio_w
		from	pls_requisicao
		where	nr_sequencia	= nr_seq_requisicao_p;
	exception
	when others then
		ie_estagio_w	:= 0;
	end;

	if (ie_estagio_w	= 5) then
		ie_retorno_w	:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
