CREATE OR REPLACE FUNCTION pls_obter_se_auditor_grupo ( nr_seq_grupo_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';


BEGIN

begin
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	pls_membro_grupo_aud
where	nr_seq_grupo = nr_seq_grupo_p
and	nm_usuario_exec = nm_usuario_p
and	ie_situacao = 'A';
exception
when others then
	ds_retorno_w := 'N';
end;

return	ds_retorno_w;

end;
$body$
