SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_auditor_lib_oc ( nr_seq_ocorrencia_p text, nm_usuario_p text, nr_seq_grupo_p bigint, ie_origem_conta_p text ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'S';	/*S - O Auditor pode agir na ocorrencia - N - O Auditor nao pode agir na ocorrencia */
nr_seq_nivel_lib_aud_w		bigint;
nr_seq_nivel_lib_oc_w		bigint;
nr_nivel_liberacao_oc_w		bigint;
nr_nivel_liberacao_aud_w	bigint;
nr_seq_oc_grupo_w		bigint;
qt_oc_grupo_w			bigint;


BEGIN

begin
select	nr_sequencia
into STRICT	nr_seq_oc_grupo_w
from	pls_ocorrencia_grupo
where	nr_seq_grupo 	   = nr_seq_grupo_p
and	nr_seq_ocorrencia   = nr_seq_ocorrencia_p
and	ie_situacao 	= 'A';
exception
when others then
	nr_seq_oc_grupo_w := null;
end;

if (coalesce(nr_seq_oc_grupo_w,0) = 0) then
	
	select	count(nr_sequencia)
	into STRICT	qt_oc_grupo_w
	from	pls_ocorrencia_grupo
	where	nr_seq_ocorrencia   = nr_seq_ocorrencia_p
	and	ie_situacao 	= 'A';

	if (qt_oc_grupo_w > 0) then	
		/*Se o o grupo nao existir na ocorrencia ele nao deve fazer a analise.*/
	
		ds_retorno_w	:= 'N';	
	end if;		
end if;

if (ds_retorno_w = 'S') then
	begin
	select	nr_seq_nivel_lib
	into STRICT	nr_seq_nivel_lib_oc_w
	from	pls_ocorrencia
	where	nr_sequencia = nr_seq_ocorrencia_p;
	exception
	when others then
		nr_seq_nivel_lib_oc_w := null;
	end;

	/*Se a ocorrencia possui nivel minimo de liberacao*/

	if (coalesce(nr_seq_nivel_lib_oc_w,0) > 0) then

		/*Verifica-se se o auditor ou grupo deste possui nivel informado*/

		begin
		select	coalesce(b.nr_seq_nivel_lib, a.nr_seq_nivel_lib)
		into STRICT	nr_seq_nivel_lib_aud_w
		from	pls_grupo_auditor 	a,
			pls_membro_grupo_aud 	b
		where	a.nr_sequencia 		= b.nr_seq_grupo
		and	b.nm_usuario_exec 	= nm_usuario_p
		and	a.nr_sequencia 	   	= nr_seq_grupo_p;
		exception
		when others then
			nr_seq_nivel_lib_aud_w	:= null;
		end;
		
		/*Obter o nivel do auditor*/

		begin
		select	nr_nivel_liberacao
		into STRICT	nr_nivel_liberacao_aud_w
		from	pls_nivel_liberacao
		where	nr_sequencia = nr_seq_nivel_lib_aud_w;
		exception
		when others then
			nr_nivel_liberacao_aud_w := null;
		end;
		
		/*Obter o nivel do ocorrencia*/

		begin
		select	nr_nivel_liberacao
		into STRICT	nr_nivel_liberacao_oc_w
		from	pls_nivel_liberacao
		where	nr_sequencia = nr_seq_nivel_lib_oc_w;
		exception
		when others then
			nr_nivel_liberacao_oc_w	:= null;
		end;
		
		/*Se o nivel da ocorrencia for maior que o nivel do auditor ele nao pode agir*/

		if (coalesce(nr_nivel_liberacao_oc_w,0) > coalesce(nr_nivel_liberacao_aud_w,0)) then
			ds_retorno_w := 'N';
		end if;
		
	end if;
	
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_auditor_lib_oc ( nr_seq_ocorrencia_p text, nm_usuario_p text, nr_seq_grupo_p bigint, ie_origem_conta_p text ) FROM PUBLIC;

