CREATE OR REPLACE FUNCTION pls_obter_se_auditor_nivel_lib ( nr_seq_auditoria_p bigint, nr_seq_item_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text, ie_auditoria_master_p text) RETURNS varchar AS $body$
DECLARE


ie_permissao_w			varchar(1);
nr_nivel_auditor_w		bigint;
qt_itens_total_w		bigint;
nr_nivel_liberacao_min_w	bigint;
ds_retorno_w			varchar(1) := 'N';
nr_seq_ocorrencia_w		bigint;
qt_itens_aud_pertence_w		integer;


BEGIN

begin
	select	ie_permissao
	into STRICT	ie_permissao_w
	from	pls_grupo_auditor
	where	nr_sequencia = nr_seq_grupo_p
	and	ie_situacao = 'A';
exception
when others then
	ie_permissao_w := null;
end;

if (ie_permissao_w = 'T') then
	ds_retorno_w := 'S';
else
	nr_nivel_auditor_w	:= coalesce((pls_obter_dados_auditor(null,null,nm_usuario_p,'L',nr_seq_grupo_p))::numeric ,0);
end if;

if (ds_retorno_w <> 'S') then
	if (ie_auditoria_master_p = 'N') then
		select	count(1)
		into STRICT	qt_itens_total_w
		from	pls_analise_ocor_glosa_aut
		where	nr_seq_aud_item = nr_seq_item_p;

		if (qt_itens_total_w > 0) then
			if (coalesce(ie_permissao_w,'X') <> 'R') then
				select	count(1)
				into STRICT	qt_itens_aud_pertence_w
				from	pls_analise_ocor_glosa_aut
				where	nr_seq_aud_item = nr_seq_item_p
				and	'S' = pls_obter_grupo_pertence_ocorr(nr_seq_ocorrencia,nr_seq_grupo_p);
			else
				qt_itens_aud_pertence_w := 1;
			end if;

			if (qt_itens_aud_pertence_w > 0) then
				select	min(nr_nivel_liberacao)
				into STRICT	nr_nivel_liberacao_min_w
				from	pls_analise_ocor_glosa_aut
				where	nr_seq_aud_item = nr_seq_item_p;

				if (nr_nivel_auditor_w >= nr_nivel_liberacao_min_w) then
					ds_retorno_w := 'S';
				end if;

			end if;
		else
			ds_retorno_w := 'S';
		end if;

	elsif (ie_auditoria_master_p = 'S') then
		select	count(1)
		into STRICT	qt_itens_total_w
		from	pls_analise_ocor_glosa_aut
		where	nr_seq_auditoria = nr_seq_auditoria_p;

		if (qt_itens_total_w > 0) then
			if (coalesce(ie_permissao_w,'X') <> 'R') then
				select	count(1)
				into STRICT	qt_itens_aud_pertence_w
				from	pls_analise_ocor_glosa_aut
				where	nr_seq_auditoria = nr_seq_auditoria_p
				and	'S' = pls_obter_grupo_pertence_ocorr(nr_seq_ocorrencia,nr_seq_grupo_p);
			else
				qt_itens_aud_pertence_w := 1;
			end if;

			if (qt_itens_aud_pertence_w > 0) then
				select	min(nr_nivel_liberacao)
				into STRICT	nr_nivel_liberacao_min_w
				from	pls_analise_ocor_glosa_aut
				where	nr_seq_auditoria = nr_seq_auditoria_p;

				if (nr_nivel_auditor_w >= nr_nivel_liberacao_min_w) then
					ds_retorno_w := 'S';
				end if;
			end if;
		else
			ds_retorno_w := 'S';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
