CREATE OR REPLACE FUNCTION pls_obter_se_aud_fechar_conta ( nr_seq_analise_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
nr_seq_analise_conta_item_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_analise_conta_item
	where	nr_seq_analise = nr_seq_analise_p
	and	ie_status = 'P'
	and	ie_tipo = 'O'
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_analise_conta_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_retorno_w := 'S';

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
