CREATE OR REPLACE FUNCTION pls_obter_se_benef_pag_menor ( nr_seq_proc_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
vl_calculado_w			double precision;
vl_beneficiario_w		double precision;


BEGIN

select	coalesce(vl_procedimento,0),
	coalesce(vl_beneficiario,0)
into STRICT	vl_calculado_w,
	vl_beneficiario_w
from	pls_conta_proc
