SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_benef_remido ( nr_seq_segurado_p pls_sca_vinculo.nr_seq_segurado%type, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE

ie_remido_w	varchar(1);
qt_remido_w	bigint;


BEGIN
ie_remido_w := 'N';

select	count(1)
into STRICT	qt_remido_w
from	pls_bonificacao_vinculo	a,
	pls_bonificacao		b
where	b.nr_sequencia		= a.nr_seq_bonificacao
and	a.nr_seq_segurado	= nr_seq_segurado_p
and	b.ie_seguro_obito	= 'S'
and	dt_referencia_p between trunc(coalesce(a.dt_inicio_vigencia, dt_referencia_p),'dd') and fim_dia(coalesce(a.dt_fim_vigencia, dt_referencia_p));

if (qt_remido_w = 0) then
	select	count(1)
	into STRICT	qt_remido_w
	from	pls_sca_vinculo	a,
		pls_plano	b
	where	b.nr_sequencia	= a.nr_seq_plano
	and	a.nr_seq_segurado = nr_seq_segurado_p
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	b.ie_seguro_obito = 'B'
	and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
	and	dt_referencia_p between trunc(coalesce(a.dt_inicio_vigencia, dt_referencia_p),'dd') and fim_dia(coalesce(a.dt_fim_vigencia, dt_referencia_p));
end if;

if (qt_remido_w > 0) then
	ie_remido_w := 'S';
end if;

return ie_remido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
