SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_benef_titulo ( nr_titulo_p bigint, cd_carteira_p text) RETURNS varchar AS $body$
DECLARE


ie_carteira_w		varchar(1)	:= 'N';
nr_seq_segurado_w	bigint;
nr_seq_mensalidade_w	bigint;

c_segurado CURSOR FOR
	SELECT	a.nr_seq_segurado
	from	pls_segurado_carteira	a
	where	a.cd_usuario_plano	like cd_carteira_p;


BEGIN
select	max(a.nr_seq_mensalidade)
into STRICT	nr_seq_mensalidade_w
from	titulo_receber	a
where	a.nr_titulo	= nr_titulo_p;

open c_segurado;
loop
fetch c_segurado into
	nr_seq_segurado_w;
EXIT WHEN NOT FOUND; /* apply on c_segurado */
	begin
	if (nr_seq_mensalidade_w IS NOT NULL AND nr_seq_mensalidade_w::text <> '') then
		select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_carteira_w
		from	pls_mensalidade			b,
			pls_mensalidade_segurado	c,
			pls_segurado			d
		where	d.nr_sequencia	= c.nr_seq_segurado
		and	b.nr_sequencia	= c.nr_seq_mensalidade
		and	b.nr_sequencia	= nr_seq_mensalidade_w
		and	d.nr_sequencia	= nr_seq_segurado_w;
	else
		if (ie_carteira_w = 'N') then
			select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ie_carteira_w
			from	titulo_receber		a
			where	a.nr_seq_pagador in (SELECT	b.nr_sequencia
							from	pls_contrato_pagador	b,
								pls_segurado		c
							where	b.nr_sequencia	= c.nr_seq_pagador
							and	c.nr_sequencia	= nr_seq_segurado_w);
		end if;
	end if;
	end;
end loop;
close c_segurado;

return	ie_carteira_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
