SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_bene_guia_liminar ( nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se o beneficiário possui liminar judicial cadastrada na função OPS - Processo judicial.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [X] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Avisar ao ao pessoal do portal e do Delphi/Java sempre que for alterado ou incluido algum parâmetro .
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_area_w			bigint;
cd_especialidade_w		bigint;
cd_grupo_w			bigint;
cd_procedimento_w		bigint;

ie_origem_proced_w		bigint;
ie_origem_proc_w		bigint;
ie_retorno_w			varchar(2)	:= 'N';

qt_reg_proc_w			bigint;
qt_reg_mat_w			bigint;
qt_reg_prest_w			bigint;
qt_registros_w			bigint;

nr_seq_segurado_w		bigint;
nr_seq_prestador_w		bigint;
nr_seq_material_w		bigint;
nr_seq_contrato_w		bigint;

cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;

C01 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced
	from	pls_guia_plano_proc
	where	nr_seq_guia	= nr_seq_guia_p;

C02 CURSOR FOR
	SELECT	nr_seq_material
	from	pls_guia_plano_mat
	where	nr_seq_guia	= nr_seq_guia_p;


BEGIN

select	nr_seq_segurado,
	nr_seq_prestador
into STRICT	nr_seq_segurado_w,
	nr_seq_prestador_w
from	pls_guia_plano
where	nr_sequencia	= nr_seq_guia_p;

begin
	select	nr_seq_contrato,
		cd_pessoa_fisica
	into STRICT	nr_seq_contrato_w,
		cd_pessoa_fisica_w
	from	pls_segurado
	where	nr_sequencia = nr_seq_segurado_w;
exception
when others then
	nr_seq_contrato_w := 0;
end;

select	count(1)
into STRICT	qt_registros_w
from	processo_judicial_liminar
where	(((coalesce(ie_considera_codigo_pf, 'N') = 'N'	and nr_seq_segurado	= nr_seq_segurado_w)
or (coalesce(ie_considera_codigo_pf, 'N') = 'S'		and pls_obter_dados_segurado(nr_seq_segurado, 'PF')	= cd_pessoa_fisica_w))
or	nr_seq_contrato		= nr_seq_contrato_w)
and	ie_estagio		= 2
and	ie_impacto_autorizacao	= 'S'
and	clock_timestamp()	between(dt_inicio_validade) and (coalesce(dt_fim_validade,clock_timestamp()));

if (qt_registros_w	> 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_bene_guia_liminar ( nr_seq_guia_p bigint) FROM PUBLIC;

