CREATE OR REPLACE FUNCTION pls_obter_se_bonific_funcao ( cd_funcao_p bigint, dt_parametro_p timestamp, nr_seq_bonificacao_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);
qt_registros_w		bigint;
qt_liberacao_w		bigint;

C01 CURSOR FOR
	SELECT	cd_perfil
	from	usuario_perfil
	where	nm_usuario = nm_usuario_p;


BEGIN
select	count(*)
into STRICT	qt_registros_w
from	pls_bonificacao_liberacao;

if (qt_registros_w	> 0) then
	select	count(*)
	into STRICT	qt_liberacao_w
	from	pls_bonificacao_liberacao	a,
		pls_bonificacao			b
	where	a.nr_seq_bonificacao = b.nr_sequencia
	and	cd_funcao		= cd_funcao_p
	and	nr_seq_bonificacao	= nr_seq_bonificacao_p
	and	dt_parametro_p between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia,dt_parametro_p+1)
	and	dt_parametro_p between b.dt_inicio_vigencia and coalesce(b.dt_fim_vigencia,dt_parametro_p+1)
	and (nm_usuario_liberacao	= nm_usuario_p or coalesce(a.nm_usuario_liberacao::text, '') = '')
	and (a.cd_perfil in (SELECT	cd_perfil
			 	 from	usuario_perfil
			         where	nm_usuario = nm_usuario_p)
	or	coalesce(a.cd_perfil::text, '') = '');

		if (qt_liberacao_w	> 0) then
			ie_retorno_w	:= 'S';
		else
			ie_retorno_w	:= 'N';
		end if;
else
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
