CREATE OR REPLACE FUNCTION pls_obter_se_carac_benef ( nr_seq_segurado_p bigint, nr_seq_contrato_p bigint, dt_solicitacao_p timestamp, nr_seq_caracteristica_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';

C01 CURSOR FOR
	SELECT	nr_seq_caracteristica
	from	pls_seg_caracteristica
	where	nr_seq_segurado = nr_seq_segurado_p
	and	trunc(dt_solicitacao_p) between trunc(coalesce(dt_inicio_vigencia, clock_timestamp())) and trunc(coalesce(dt_fim_vigencia, clock_timestamp()));
	
C02 CURSOR FOR
	SELECT	nr_seq_caracteristica
	from	pls_contrato_caracterist
	where	nr_seq_contrato	= nr_seq_contrato_p
	and	trunc(dt_solicitacao_p) between trunc(coalesce(dt_inicio_vigencia, clock_timestamp())) and trunc(coalesce(dt_fim_vigencia, clock_timestamp()));
BEGIN

if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	for r_c01_w in C01 loop
		if (nr_seq_caracteristica_p = r_c01_w.nr_seq_caracteristica) then
			ds_retorno_w := 'S';
			exit;		
		end if;
	end loop;
elsif (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then
	for r_c02_w in C02 loop
		if (nr_seq_caracteristica_p = r_c02_w.nr_seq_caracteristica) then
			ds_retorno_w := 'S';
			exit;		
		end if;
	end loop;
end if;

return	ds_retorno_w;

end;
$body$
