CREATE OR REPLACE FUNCTION pls_obter_se_carencia_benef ( nr_seq_segurado_p bigint, ie_opcao_p text, ie_situacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
nr_seq_plano_w		bigint;
nr_seq_contrato_w		bigint;
dt_adesao_w		timestamp;
qt_carencias_plano_w	bigint;
qt_carencias_seg_w	bigint;
qt_carencias_contr_w	bigint;
qt_carencias_plano_ww	bigint;
qt_carencias_seg_ww	bigint;
qt_carencias_contr_ww	bigint;


BEGIN

select	dt_contratacao,
	nr_seq_contrato,
	--nr_seq_plano
	pls_obter_produto_benef(nr_sequencia,dt_contratacao) as nr_seq_plano
into STRICT	dt_adesao_w,
	nr_seq_contrato_w,
	nr_seq_plano_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

/*select	decode(ie_situacao_p,'C','A','C')
into	ds_retorno_w
from	dual;*/
select	count(*)
into STRICT	qt_carencias_contr_ww
from	pls_carencia
where	nr_seq_contrato = nr_seq_contrato_w;

select	count(*)
into STRICT	qt_carencias_plano_ww
from	pls_carencia
where	nr_seq_plano = nr_seq_plano_w;

select	count(*)
into STRICT	qt_carencias_seg_ww
from	pls_carencia
where	nr_seq_segurado = nr_seq_segurado_p;

if (ie_opcao_p = 'T') then
	if (ie_situacao_p = 'C') then
		select	count(*)
		into STRICT	qt_carencias_contr_w
		from	pls_carencia
		where	nr_seq_contrato = nr_seq_contrato_w
		and	add_months(dt_adesao_w, qt_dias/30) <= clock_timestamp();

		select	count(*)
		into STRICT	qt_carencias_plano_w
		from	pls_carencia
		where	nr_seq_plano = nr_seq_plano_w
		and	add_months(dt_adesao_w, qt_dias/30) <= clock_timestamp();

		select	count(*)
		into STRICT	qt_carencias_seg_w
		from	pls_carencia
		where	nr_seq_segurado = nr_seq_segurado_p
		and	add_months(dt_adesao_w, qt_dias/30) <= clock_timestamp();

		if	(qt_carencias_contr_w > 0 AND qt_carencias_contr_ww > 0) or
			(qt_carencias_seg_w > 0 AND qt_carencias_seg_ww > 0) or
			(qt_carencias_plano_w > 0 AND qt_carencias_plano_ww > 0) then
			ds_retorno_w	:= 'C';
		else
			ds_retorno_w	:= 'A';
		end if;
	elsif (ie_situacao_p = 'A') then
				select	count(*)
		into STRICT	qt_carencias_contr_w
		from	pls_carencia
		where	nr_seq_contrato = nr_seq_contrato_w
		and	add_months(dt_adesao_w, qt_dias/30) > clock_timestamp();

		select	count(*)
		into STRICT	qt_carencias_plano_w
		from	pls_carencia
		where	nr_seq_plano = nr_seq_plano_w
		and	add_months(dt_adesao_w, qt_dias/30) > clock_timestamp();

		select	count(*)
		into STRICT	qt_carencias_seg_w
		from	pls_carencia
		where	nr_seq_segurado = nr_seq_segurado_p
		and	add_months(dt_adesao_w, qt_dias/30) > clock_timestamp();

		if	(qt_carencias_contr_w > 0 AND qt_carencias_contr_ww > 0) or
			(qt_carencias_seg_w > 0 AND qt_carencias_seg_ww > 0) or
			(qt_carencias_plano_w > 0 AND qt_carencias_plano_ww > 0) then
			ds_retorno_w	:= 'A';
		else
			ds_retorno_w	:= 'C';
		end if;
	end if;
elsif (ie_opcao_p = 'S') then
	if (ie_situacao_p = 'C') then
		select	count(*)
		into STRICT	qt_carencias_seg_w
		from	pls_carencia
		where	nr_seq_segurado = nr_seq_segurado_p
		and	add_months(dt_adesao_w, qt_dias/30) <= clock_timestamp();

		if	(qt_carencias_seg_w > 0 AND qt_carencias_seg_ww > 0)  then
			ds_retorno_w	:= 'C';
		else
			ds_retorno_w	:= 'A';
		end if;
	elsif (ie_situacao_p = 'A') then
		select	count(*)
		into STRICT	qt_carencias_seg_w
		from	pls_carencia
		where	nr_seq_segurado = nr_seq_segurado_p
		and	add_months(dt_adesao_w, qt_dias/30) > clock_timestamp();

		if	(qt_carencias_seg_w > 0 AND qt_carencias_seg_ww > 0)  then
			ds_retorno_w	:= 'A';
		else
			ds_retorno_w	:= 'C';
		end if;
	end if;
elsif (ie_opcao_p = 'C') then
	if (ie_situacao_p  = 'C') then
		select	count(*)
		into STRICT	qt_carencias_contr_w
		from	pls_carencia
		where	nr_seq_contrato = nr_seq_contrato_w
		and	add_months(dt_adesao_w, qt_dias/30) <= clock_timestamp();

		if	(qt_carencias_contr_w > 0 AND qt_carencias_contr_ww > 0)  then
			ds_retorno_w	:= 'C';
		else
			ds_retorno_w	:= 'A';
		end if;
	elsif (ie_situacao_p = 'A') then
		select	count(*)
		into STRICT	qt_carencias_contr_w
		from	pls_carencia
		where	nr_seq_contrato = nr_seq_contrato_w
		and	add_months(dt_adesao_w, qt_dias/30) > clock_timestamp();

		if	(qt_carencias_contr_w > 0 AND qt_carencias_contr_ww > 0)  then
			ds_retorno_w	:= 'A';
		else
			ds_retorno_w	:= 'C';
		end if;
	end if;
elsif (ie_opcao_p = 'P') then
	if (ie_situacao_p = 'C') then
		select	count(*)
		into STRICT	qt_carencias_plano_w
		from	pls_carencia
		where	nr_seq_plano = nr_seq_plano_w
		and	add_months(dt_adesao_w, qt_dias/30) <= clock_timestamp();

		if	(qt_carencias_plano_w > 0 AND qt_carencias_plano_ww > 0)  then
			ds_retorno_w	:= 'C';
		else
			ds_retorno_w	:= 'A';
		end if;
	elsif (ie_situacao_p = 'A') then
		select	count(*)
		into STRICT	qt_carencias_plano_w
		from	pls_carencia
		where	nr_seq_plano = nr_seq_plano_w
		and	add_months(dt_adesao_w, qt_dias/30) > clock_timestamp();

		if	(qt_carencias_plano_w > 0 AND qt_carencias_plano_ww > 0)  then
			ds_retorno_w	:= 'A';
		else
			ds_retorno_w	:= 'C';
		end if;
	end if;
end if;



return	ds_retorno_w;

end;
$body$
