CREATE OR REPLACE FUNCTION pls_obter_se_carteira_vencida ( nr_seq_conta_p bigint, nr_seq_segurado_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


nr_seq_segurado_w		bigint;
cd_usuario_plano_w		varchar(30);
ie_retorno_w			varchar(2) := 'N';
dt_validade_cart_w		timestamp;
dt_atendimento_referencia_w	timestamp;


BEGIN

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then
	if (nr_seq_segurado_p <> 0) then
		cd_usuario_plano_w := pls_obter_carteira_segurado(nr_seq_segurado_p);
		begin
		select	coalesce(dt_validade_carteira,clock_timestamp())
		into STRICT	dt_validade_cart_w
		from	pls_segurado_carteira
		where	nr_seq_segurado   = nr_seq_segurado_p
		and	cd_usuario_plano  = cd_usuario_plano_w;
		exception
		when others then
			dt_validade_cart_w := clock_timestamp();
		end;

		if (coalesce(dt_referencia_p::text, '') = '') then
			select	coalesce(dt_atendimento_referencia,clock_timestamp())
			into STRICT	dt_atendimento_referencia_w
			from	pls_conta
			where	nr_sequencia = nr_seq_conta_p;
		else
			dt_atendimento_referencia_w	:= dt_referencia_p;
		end if;

		if (dt_validade_cart_w >= dt_atendimento_referencia_w) then
			ie_retorno_w := 'V';
		else
			ie_retorno_w := 'VC';
		end if;
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
