CREATE OR REPLACE FUNCTION pls_obter_se_cart_emite_boleto (nr_seq_carteira_cobr_p bigint, ds_carteiras_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
cd_carteira_w		varchar(40);
ds_virgula_w		varchar(1);
qt_registro_w		bigint := 0;


BEGIN
if (nr_seq_carteira_cobr_p IS NOT NULL AND nr_seq_carteira_cobr_p::text <> '') then
	select	cd_carteira
	into STRICT	cd_carteira_w
	from	banco_carteira
	where	nr_sequencia = nr_seq_carteira_cobr_p;

	select	position(',' || cd_carteira_w || ',' in replace(','|| ds_carteiras_p || ',',' ',''))
	into STRICT	qt_registro_w
	;

	if (qt_registro_w > 0) then
		ds_retorno_w := 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
