SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_cid_bloqueado ( ie_tipo_guia_p text, cd_doenca_p text) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Verificar se o CID está na regra utilizada para bloquear determinados campos
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_retorno_w			varchar(255) := 'N';
nr_seq_regra_w			bigint;
ds_doenca_w			varchar(255);


BEGIN
if (pls_obter_se_controle_estab('RE') = 'S') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_regra_bloq_atributo
	where	coalesce(ie_tipo_guia,ie_tipo_guia_p)	= ie_tipo_guia_p
	and	cd_doenca				= cd_doenca_p
	and	ie_situacao				= 'A'
	and (cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento);
else
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_regra_bloq_atributo
	where	coalesce(ie_tipo_guia,ie_tipo_guia_p)	= ie_tipo_guia_p
	and	cd_doenca				= cd_doenca_p
	and	ie_situacao				= 'A';
end if;

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	ds_retorno_w	:= 'S';
else
	begin
	select	coalesce(ds_doenca_cid,'X')
	into STRICT	ds_doenca_w
        from	cid_doenca
        where	upper(cd_doenca_cid)	= upper(cd_doenca_p)
	and	ie_situacao		= 'A';
	exception
	when others then
		ds_doenca_w	:= 'X';
	end;

	if (ds_doenca_w	<> 'X') then
		ds_retorno_w	:= ds_doenca_w;
	else
		ds_retorno_w	:= 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_cid_bloqueado ( ie_tipo_guia_p text, cd_doenca_p text) FROM PUBLIC;

