CREATE OR REPLACE FUNCTION pls_obter_se_cid_guia_req ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, cd_cid_p text, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
qt_reg_w			bigint;


BEGIN

if (nr_seq_item_p in (1,2,7)) then

	select	count(*)
	into STRICT	qt_reg_w
	from	pls_diagnostico
	where	nr_seq_guia	= nr_seq_guia_p
	and	cd_doenca	= cd_cid_p;

elsif (nr_seq_item_p in (5,6,9)) then

	select	count(*)
	into STRICT	qt_reg_w
	from	pls_requisicao_diagnostico
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and	cd_doenca	= cd_cid_p;
end if;

if (qt_reg_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
