CREATE OR REPLACE FUNCTION pls_obter_se_cid_procedimento ( nr_seq_conta_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'S';
qt_proc_cid_w			integer;


BEGIN

select	count(*)
into STRICT	qt_proc_cid_w
from	pls_procedimento_cid
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

if (qt_proc_cid_w > 0) then
	select	count(*)
	into STRICT	qt_proc_cid_w
	from	pls_procedimento_cid a
	where	a.cd_procedimento	= cd_procedimento_p
	and	a.ie_origem_proced	= ie_origem_proced_p
	and	exists (SELECT	1
			from	pls_diagnostico_conta x
			where	x.nr_seq_conta = nr_seq_conta_p
			and	x.cd_doenca = a.cd_doenca_cid);

	if (qt_proc_cid_w = 0) then
		ds_retorno_w := 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
