SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_cob_prev_servico ( nr_seq_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE


dt_requisicao_w			timestamp;
ie_tipo_guia_w			varchar(2);
nr_seq_segurado_w		bigint;
nr_seq_contrato_w		bigint;
nr_seq_regra_w			bigint	:= 0;
ds_retorno_w			varchar(1) 	:= 'N';
qt_registros_w			bigint;
nr_seq_intercambio_w		bigint	:= 0;
qt_cobr_prev_w			bigint	:= 0;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	(	SELECT	nr_sequencia,
				ie_tipo_guia,
				nr_seq_contrato
			from	pls_regra_cob_previa_serv
			where	((coalesce(ie_tipo_guia::text, '') = '')	or (ie_tipo_guia	= ie_tipo_guia_w))
			and	((coalesce(nr_seq_contrato::text, '') = '')	or (nr_seq_contrato	= nr_seq_contrato_w))
			and	((coalesce(nr_seq_contrato_int::text, '') = '')	or (nr_seq_contrato_int	= nr_seq_intercambio_w))
			and	clock_timestamp()	between dt_inicio_vigencia	and fim_dia(coalesce(dt_fim_vigencia,clock_timestamp()))
			and (coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento and pls_obter_se_controle_estab('RE') = 'S')
			
union all

			select	nr_sequencia,
				ie_tipo_guia,
				nr_seq_contrato
			from	pls_regra_cob_previa_serv
			where	((coalesce(ie_tipo_guia::text, '') = '')	or (ie_tipo_guia	= ie_tipo_guia_w))
			and	((coalesce(nr_seq_contrato::text, '') = '')	or (nr_seq_contrato	= nr_seq_contrato_w))
			and	((coalesce(nr_seq_contrato_int::text, '') = '')	or (nr_seq_contrato_int	= nr_seq_intercambio_w))
			and	clock_timestamp()	between dt_inicio_vigencia	and fim_dia(coalesce(dt_fim_vigencia,clock_timestamp()))
			and 	pls_obter_se_controle_estab('RE') = 'N') alias36
	order by coalesce(ie_tipo_guia,'0'),
		 coalesce(nr_seq_contrato,0);


BEGIN

if (coalesce(nr_seq_requisicao_p,0) > 0) then
	select	dt_requisicao,
		ie_tipo_guia,
		nr_seq_segurado
	into STRICT	dt_requisicao_w,
		ie_tipo_guia_w,
		nr_seq_segurado_w
	from	pls_requisicao
	where	nr_sequencia = nr_seq_requisicao_p;

	begin
		select	coalesce(nr_seq_contrato,0),
			coalesce(nr_seq_intercambio,0)
		into STRICT	nr_seq_contrato_w,
			nr_seq_intercambio_w
		from	pls_segurado
		where	nr_sequencia = nr_seq_segurado_w;
	exception
	when others then
		nr_seq_contrato_w	:= 0;
		nr_seq_intercambio_w	:= 0;
	end;

	open C01;
	loop
	fetch C01 into
		nr_seq_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
end if;

if (nr_seq_regra_w > 0) then
	select	count(1)
	into STRICT	qt_registros_w
	from	pls_orcamento
	where	nr_seq_requisicao	= nr_seq_requisicao_p;

	if (qt_registros_w	> 0) then
		select	count(1)
		into STRICT	qt_cobr_prev_w
		from	pls_cobranca_previa_serv
		where	nr_seq_requisicao	= nr_seq_requisicao_p
		and	ie_situacao	= 'C';

		if (qt_registros_w = qt_cobr_prev_w) then
			ds_retorno_w := 'S';
		else
			ds_retorno_w := 'N';
		end if;
	else
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
