CREATE OR REPLACE FUNCTION pls_obter_se_compl_pedido_aut ( nr_seq_requisicao_p bigint, nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(2)	:= 'N';
nr_seq_guia_princ_w	bigint;
nr_seq_requisicao_w	bigint;
qt_registros_w		bigint;


BEGIN

if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	begin
		select	nr_seq_guia_principal
		into STRICT	nr_seq_guia_princ_w
		from	pls_requisicao
		where	nr_sequencia	= nr_seq_requisicao_p;
	exception
	when others then
		nr_seq_guia_princ_w	:= 0;
	end;

	if (coalesce(nr_seq_guia_princ_w,0)	<> 0) then
		select	max(nr_seq_requisicao)
		into STRICT	nr_seq_requisicao_w
		from	pls_execucao_req_item
		where	nr_seq_guia	= nr_seq_guia_princ_w;

		if (coalesce(nr_seq_requisicao_w,0)	<> 0) then
			select	count(1)
			into STRICT	qt_registros_w
			from	ptu_pedido_autorizacao
			where	nr_seq_requisicao	= nr_seq_requisicao_w;

			if (qt_registros_w	> 0) then
				ie_retorno_w	:= 'S';
			end if;
		end if;
	end if;
elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	begin
		select	nr_seq_guia_principal
		into STRICT	nr_seq_guia_princ_w
		from	pls_guia_plano
		where	nr_sequencia	= nr_seq_guia_p;
	exception
	when others then
		nr_seq_guia_princ_w	:= 0;
	end;

	select	count(1)
	into STRICT	qt_registros_w
	from	ptu_pedido_autorizacao
	where	nr_seq_guia	= nr_seq_guia_princ_w;

	if (qt_registros_w	> 0) then
		ie_retorno_w	:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
