SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_comp_fechada (dt_mes_competencia_p pls_protocolo_conta.dt_mes_competencia%type) RETURNS varchar AS $body$
DECLARE

qt_lote_w	integer;
ds_retorno_w	varchar(1)	:= 'N';
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;


BEGIN

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

select	count(1)
into STRICT	qt_lote_w
from	lote_contabil		a
where	a.cd_tipo_lote_contabil	= 40
and	trunc(a.dt_referencia,'dd') = dt_mes_competencia_p
and	coalesce(a.cd_estabelecimento, cd_estabelecimento_w) = cd_estabelecimento_w
and	(a.dt_geracao_lote IS NOT NULL AND a.dt_geracao_lote::text <> '');

if (qt_lote_w	> 0) then
	begin
	ds_retorno_w	:= 'S';
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
