CREATE OR REPLACE FUNCTION pls_obter_se_consid_proc_ref ( nr_seq_proc_ref_p pls_conta_proc.nr_seq_proc_ref%type) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Obter o status do procedimento principal passado por parâmetro
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

Alterações:
-------------------------------------------------------------------------------------------------------------------
jjung 27/08/2013 - OS 596834 - Criação da function.
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_status_proc_ref_w	pls_conta_proc.ie_status%type;


BEGIN

-- Se o procedimento não tiver procedimento principal então é para considerar
if (coalesce(nr_seq_proc_ref_p::text, '') = '') then

	return 'S';
else
	-- se tiver procedimento principal então busca o status do mesmo.
	select	max(a.ie_status)
	into STRICT	ie_status_proc_ref_w
	from	pls_conta_proc a
	where	a.nr_sequencia = nr_seq_proc_ref_p;

	-- Se não tiver este procedimento na base ou o mesmo estiver cancelado então considera o procedimento.
	if (coalesce(ie_status_proc_ref_w::text, '') = '' or ie_status_proc_ref_w = 'D') then

		return 'S';
	else
		-- se não não considera.
		return 'N';
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
