CREATE OR REPLACE FUNCTION pls_obter_se_cons_vl_apre_prot ( ie_origem_protocolo_p pls_protocolo_conta.ie_origem_protocolo%type, ie_tipo_guia_p pls_protocolo_conta.ie_origem_protocolo%type, ie_apresentacao_p pls_protocolo_conta.ie_apresentacao%type) RETURNS varchar AS $body$
DECLARE


nr_seq_regra_vl_apres_w		pls_cta_reg_apres.nr_sequencia%type;
qt_registros_w			integer;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_regra_vl_apres_w
from	pls_cta_reg_apres
where	ie_valida_vl_apresentado = 'S';

--Verifica se tem regra cadastrada
if (nr_seq_regra_vl_apres_w IS NOT NULL AND nr_seq_regra_vl_apres_w::text <> '') then
	--Verifica se na regra tem registros, caso tem então consiste conforme a origem do protocolo, caso não tiver libera
	select	count(1)
	into STRICT	qt_registros_w
	from	pls_cta_reg_apres_prot
	where	nr_seq_regra_vl_apres	= nr_seq_regra_vl_apres_w;

	if (qt_registros_w > 0) then
		select	count(1)
		into STRICT	qt_registros_w
		from	pls_cta_reg_apres_prot
		where	nr_seq_regra_vl_apres	= nr_seq_regra_vl_apres_w
		and		((ie_origem_protocolo	= ie_origem_protocolo_p) or (coalesce(ie_origem_protocolo::text, '') = ''))
		and		((ie_tipo_guia 		= ie_tipo_guia_p) or (coalesce(ie_tipo_guia::text, '') = ''))
		and  	((coalesce(ie_apresentacao::text, '') = '') or (ie_apresentacao = 'AA') or (ie_apresentacao = ie_apresentacao_p));

		if (qt_registros_w = 0) then
			return 'N';
		else
			return 'S';
		end if;
	else
		return 'S';
	end if;
end if;

return	'N';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
