SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_conta_autorizada (nr_seq_conta_p bigint, ie_consistencia_p text) RETURNS varchar AS $body$
DECLARE



ie_tipo_conta_w		pls_conta.ie_tipo_conta%type;
cd_senha_w		pls_conta.cd_senha%type;
nr_seq_guia_int_w	pls_guia_plano.nr_sequencia%type	:= null;
nr_seq_congenere_w	pls_protocolo_conta.nr_seq_congenere%type;
ie_retorno_w		varchar(1) 	:= 'N';
ie_status_autorizacao_w	bigint;
cd_guia_referencia_w	varchar(20);
nr_seq_segurado_w	bigint;


/*+++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se a guia da conta está autorizada
--------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ] Relatórios [ ] Outros:
 -------------------------------------------------------------------------------------
Pontos de atenção: Verificar também os campos de pesquisa, CD_GUIA_PESQUISA, da tabela PLS_GUIA_PLANO
+++++++++++++++++++++++++++++++++++++++++++++++*//*
ie_consistencia_p

'IA' = Importação de arquivo
'C'  = Conta
*/
/*Não está sendo tratado por nvl pois se não for encontrado na guia referencia procura na guia principal*/

BEGIN

begin
select	a.nr_seq_segurado,
	a.ie_tipo_conta,
	a.cd_senha,
	b.nr_seq_congenere
into STRICT	nr_seq_segurado_w,
	ie_tipo_conta_w,
	cd_senha_w,
	nr_seq_congenere_w
from	pls_conta		a,
	pls_protocolo_conta	b
where	b.nr_sequencia	= a.nr_seq_protocolo
and	a.nr_sequencia  = nr_seq_conta_p;
exception
when others then
	nr_seq_segurado_w	:= null;
	ie_tipo_conta_w		:= null;
	nr_seq_congenere_w	:= null;
end;


if (ie_consistencia_p = 'IA')	then

	begin
	select	a.cd_guia_solic_imp
	into STRICT	cd_guia_referencia_w
	from	pls_conta	a
	where	nr_sequencia = nr_seq_conta_p;
	exception
	when others then
		cd_guia_referencia_w	:= 'X';
	end;

	if (cd_guia_referencia_w <> 'X')	then

		select	max(ie_status)
		into STRICT	ie_status_autorizacao_w
		from	pls_guia_plano
		where	cd_guia = cd_guia_referencia_w;

		if (coalesce(ie_status_autorizacao_w,0) = 1)	then
			ie_retorno_w := 'S';
		else
			ie_retorno_w := 'N';
		end if;
	end if;

	if (ie_retorno_w = 'N')	then

		begin
			select	a.cd_guia_imp
			into STRICT	cd_guia_referencia_w
			from	pls_conta	a
			where	nr_sequencia = nr_seq_conta_p;
		exception
		when others then
			cd_guia_referencia_w	:= 'X';
		end;

		if (cd_guia_referencia_w <> 'X')	then
			select	max(ie_status)
			into STRICT	ie_status_autorizacao_w
			from	pls_guia_plano
			where	cd_guia = cd_guia_referencia_w
			and 	ie_estagio in (5,6,10)
			and	ie_situacao <> 'D';

			if (coalesce(ie_status_autorizacao_w,0) = 1)	then
				ie_retorno_w := 'S';
			else
				ie_retorno_w := 'N';
			end if;
		else
			ie_retorno_w	:= 'N';

		end if;
	end if;
else
	begin
	select	a.cd_guia_referencia
	into STRICT	cd_guia_referencia_w
	from	pls_conta	a
	where	nr_sequencia = nr_seq_conta_p;
	exception
	when others then
		cd_guia_referencia_w	:= 'X';
	end;

	if (cd_senha_w IS NOT NULL AND cd_senha_w::text <> '') and (ie_tipo_conta_w = 'I') and (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then
		select	coalesce(max(a.nr_sequencia),0)
		into STRICT	nr_seq_guia_int_w
		from	pls_guia_plano	a
		where	a.cd_senha		= cd_senha_w
		and	a.nr_seq_segurado	= nr_seq_segurado_w
		and	coalesce(nr_seq_uni_exec,0) = coalesce(nr_seq_congenere_w,0);
	end if;

	if (cd_guia_referencia_w <> 'X')	then

		select	max(ie_status)
		into STRICT	ie_status_autorizacao_w
		from	pls_guia_plano
		where	cd_guia = cd_guia_referencia_w;

		if (coalesce(ie_status_autorizacao_w,0) = 1)	then
			ie_retorno_w := 'S';
		else
			ie_retorno_w := 'N';
		end if;
	end if;

	if (ie_retorno_w = 'N')	then

		begin
		select	a.cd_guia
		into STRICT	cd_guia_referencia_w
		from	pls_conta	a
		where	nr_sequencia = nr_seq_conta_p;
		exception
		when others then
			cd_guia_referencia_w	:= 'X';
		end;

		if (cd_guia_referencia_w <> 'X')	then
			select	max(ie_status)
			into STRICT	ie_status_autorizacao_w
			from	pls_guia_plano
			where	cd_guia = cd_guia_referencia_w
			and 	ie_estagio in (5,6,10)
			and	ie_situacao <> 'D'
			and	nr_seq_segurado = nr_seq_segurado_w;

			if (coalesce(ie_status_autorizacao_w,0) = 1)	then
				ie_retorno_w := 'S';
			elsif (nr_seq_guia_int_w IS NOT NULL AND nr_seq_guia_int_w::text <> '')	then

				select	max(ie_status)
				into STRICT	ie_status_autorizacao_w
				from	pls_guia_plano
				where	nr_sequencia = nr_seq_guia_int_w
				and	ie_estagio in (5,6,10)
				and	ie_situacao <> 'D'
				and	nr_seq_segurado = nr_seq_segurado_w;

				if (coalesce(ie_status_autorizacao_w,0) = 1)	then
					ie_retorno_w := 'S';
				else
					ie_retorno_w	:= 'N';
				end if;

			else
				ie_retorno_w := 'N';
			end if;
		else
			ie_retorno_w	:= 'N';

		end if;
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_conta_autorizada (nr_seq_conta_p bigint, ie_consistencia_p text) FROM PUBLIC;

