CREATE OR REPLACE FUNCTION pls_obter_se_conta_partic ( nr_seq_conta_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(1) := 'N';
nr_seq_conta_proc_w	bigint;
qt_participante_w	bigint;


BEGIN
select	count(1)
into STRICT	qt_participante_w
from	pls_conta	a
where	nr_sequencia = nr_seq_conta_p
and	(( exists (	SELECT 	x.nr_sequencia
				from	pls_conta_proc	x
				where	nr_seq_conta	= a.nr_sequencia
				and	(nr_seq_participante_hi IS NOT NULL AND nr_seq_participante_hi::text <> '')))or ( exists (	select	y.nr_sequencia
				from	pls_conta_proc		y,
					pls_proc_participante	p
				where	y.nr_seq_conta		= a.nr_sequencia
				and	p.nr_seq_conta_proc	= y.nr_sequencia)));
if (coalesce(qt_participante_w,0) > 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
