CREATE OR REPLACE FUNCTION pls_obter_se_conta_reapre ( nr_seq_conta_p bigint) RETURNS varchar AS $body$
DECLARE

/*Function criado para obter se a conta já havia sido apresentada e foi totalmente glosada em outro momento */

ie_retorno_w		varchar(255) := 'Não foram localizadas contas anteriores referente a esta guia';
cd_guia_w		varchar(20);
cd_guia_referencia_w	varchar(20);
nr_seq_segurado_w	bigint;
ie_glosa_w		varchar(1);
qt_conta_w		bigint;



BEGIN

select 	cd_guia,
	cd_guia_referencia,
	nr_seq_segurado
into STRICT	cd_guia_w,
	cd_guia_referencia_w,
	nr_seq_segurado_w
from	pls_conta
where	nr_sequencia = nr_seq_conta_p;

select	count(1)
into STRICT	qt_conta_w
from	pls_conta
where	((cd_guia = cd_guia_w) or (cd_guia_referencia = cd_guia_referencia_W) or (cd_guia_w = cd_guia_referencia_W))
and	nr_seq_segurado = nr_seq_segurado_w
and	ie_glosa = 'S'
and	nr_sequencia <> nr_seq_conta_p;

if (qt_conta_W > 0) then
	ie_retorno_w := 'Foram localizadas contas glosadas referente a esta guia';
else
	ie_retorno_w := 'Não foram localizadas contas anteriores referente a esta guia';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
