CREATE OR REPLACE FUNCTION pls_obter_se_conta_rec_glosa (nr_seq_conta_p pls_conta.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_rec_glosa_w		varchar(1)	:= 'N';
qt_rec_glosa_w		integer;


BEGIN

select 	count(1)
into STRICT	qt_rec_glosa_w
from	pls_rec_glosa_conta
where	nr_seq_conta 	= nr_seq_conta_p;

if (qt_rec_glosa_w	> 0) then
	ie_rec_glosa_w	:= 'S';
end if;

return	ie_rec_glosa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
