CREATE OR REPLACE FUNCTION pls_obter_se_contrato_coletivo ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(2);
ie_tipo_contratacao_w		varchar(2);
nr_seq_plano_w			bigint;


BEGIN

begin
	select	nr_seq_plano
	into STRICT	nr_seq_plano_w
	from	pls_segurado
	where	nr_sequencia = nr_seq_segurado_p;
exception
when others then
	nr_seq_plano_w	:= null;
end;

begin
	select	ie_tipo_contratacao
	into STRICT	ie_tipo_contratacao_w
	from	pls_plano
	where	nr_sequencia = nr_seq_plano_w;
exception
when others then
	ie_tipo_contratacao_w	:='X';
end;

if (ie_tipo_contratacao_w	in ('CA','CE')) then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
