CREATE OR REPLACE FUNCTION pls_obter_se_contrato_limit ( nr_seq_contrato_p bigint, NR_SEQ_PLANO_CONTRATO_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);
qt_registros_w		bigint;


BEGIN

ds_retorno_w	:= 'N';

if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then
	select	count(1)
	into STRICT	qt_registros_w
	from	PLS_LIMITACAO
	where	nr_seq_contrato	= nr_seq_contrato_p
	and	((NR_SEQ_PLANO_CONTRATO = NR_SEQ_PLANO_CONTRATO_p) or (coalesce(NR_SEQ_PLANO_CONTRATO::text, '') = ''))  LIMIT 1;

	if (qt_registros_w > 0) then
		ds_retorno_w	:= 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
